% Codigo para filtragempkg load imageclear allclose allclcx = imread('sjaeroruido.tif');m = 560;%linhasn = 570;%colunasfigure,imshow(x);im1            = double(x);im_out_media   = zeros(m,n);im_out_mediana = zeros(m,n);for i = 2 : m - 1   for j = 2 : n - 1      aux = im1(i-1,j-1) + im1(i-1,j) + im1(i-1,j+1)...     + im1(i,j-1) + im1(i,j) + im1(i,j+1)...     + im1(i+1,j-1) + im1(i+1,j) + im1(i+1,j+1);     im_out_media(i,j) = aux / 9;     endend
for i = 2 : m - 1   for j = 2 : n - 1      aux = [im1(i-1,j-1) im1(i-1,j) im1(i-1,j+1)...     im1(i,j-1) im1(i,j) im1(i,j+1)...     im1(i+1,j-1) im1(i+1,j) im1(i+1,j+1)];     im_out_mediana(i,j) = median(aux);     endendfigure,imshow(im_out_media,[]);title('Eu consegui!!!!!');figure,imshow(im_out_mediana,[]);title('Eu consegui!!!!!');